MainDialog:
{
  "type": "sidebar",
  "bind": "sidebar",
  "children": [
    {
      "type": "panel",
      "scrollable": true,
      "children": [
        {
          "type": "tabs",
          "children": {
            "Data": {
              "type": "panel",
              "children": [
                {
                  "type": "accordion",
                  "bind": "volumeLoadContainer",
                  "label": "Volume"
                },
                {
                  "type": "accordion",
                  "bind": "envmapLoadContainer",
                  "label": "Environment"
                }
              ]
            },
            "Settings": {
              "type": "panel",
              "children": [
                {
                  "type": "accordion",
                  "label": "Renderer",
                  "children": [
                    {
                      "type": "field",
                      "label": "Renderer:",
                      "children": [
                        {
                          "type": "dropdown",
                          "bind": "rendererSelect",
                          "options": [
                            {
                              "value": "mip",
                              "label": "Maximum intensity projection"
                            },
                            {
                              "value": "iso",
                              "label": "Isosurface extraction"
                            },
                            {
                              "selected": true,
                              "value": "eam",
                              "label": "Emission-absorption model"
                            },
                            {
                              "value": "dos",
                              "label": "Directional occlusion shading"
                            },
                            {
                              "value": "mcs",
                              "label": "Single scattering"
                            },
                            {
                              "value": "mcm",
                              "label": "Multiple scattering"
                            },
                            {
                              "value": "mcc",
                              "label": "Multiple scattering (compute)"
                            }
                          ]
                        }
                      ]
                    },
                    {
                      "type": "panel",
                      "bind": "rendererSettingsContainer"
                    }
                  ]
                },
                {
                  "type": "accordion",
                  "label": "Tone mapper",
                  "children": [
                    {
                      "type": "field",
                      "label": "Tone mapper:",
                      "children": [
                        {
                          "type": "dropdown",
                          "bind": "toneMapperSelect",
                          "options": [
                            {
                              "value": "artistic",
                              "label": "Artistic",
                              "selected": true
                            },
                            {
                              "value": "range",
                              "label": "Range"
                            },
                            {
                              "value": "reinhard",
                              "label": "Reinhard"
                            },
                            {
                              "value": "reinhard2",
                              "label": "Reinhard 2"
                            },
                            {
                              "value": "uncharted2",
                              "label": "Uncharted 2"
                            },
                            {
                              "value": "filmic",
                              "label": "Filmic"
                            },
                            {
                              "value": "unreal",
                              "label": "Unreal"
                            },
                            {
                              "value": "aces",
                              "label": "Aces"
                            },
                            {
                              "value": "lottes",
                              "label": "Lottes"
                            },
                            {
                              "value": "uchimura",
                              "label": "Uchimura"
                            }
                          ]
                        }
                      ]
                    },
                    {
                      "type": "panel",
                      "bind": "toneMapperSettingsContainer"
                    }
                  ]
                },
                {
                  "type": "accordion",
                  "bind": "renderingContextSettingsContainer",
                  "label": "Context"
                }
              ]
            },
            "About": {
              "type": "panel",
              "bind": "about"
            }
          }
        }
      ]
    }
  ]
}

renderers.MCMRendererDialog:
{
  "type": "panel",
  "children": [
    {
      "type": "field",
      "label": "Extinction:",
      "children": [
        {
          "type": "spinner",
          "bind": "extinction",
          "logarithmic": true,
          "value": 1,
          "min": 0,
          "step": 0.1
        }
      ]
    },
    {
      "type": "field",
      "label": "Scattering albedo:",
      "children": [
        {
          "type": "slider",
          "bind": "albedo",
          "value": 0.5,
          "min": 0,
          "max": 1,
          "step": 0.1
        }
      ]
    },
    {
      "type": "field",
      "label": "Scattering bias:",
      "children": [
        {
          "type": "slider",
          "bind": "bias",
          "value": 0,
          "min": -1,
          "max": 1,
          "step": 0.2
        }
      ]
    },
    {
      "type": "field",
      "label": "Majorant ratio:",
      "children": [
        {
          "type": "slider",
          "bind": "ratio",
          "value": 1,
          "min": 0,
          "max": 1,
          "step": 0.1
        }
      ]
    },
    {
      "type": "field",
      "label": "Max bounces:",
      "children": [
        {
          "type": "spinner",
          "bind": "bounces",
          "value": 8,
          "min": 0
        }
      ]
    },
    {
      "type": "field",
      "label": "Steps:",
      "children": [
        {
          "type": "spinner",
          "bind": "steps",
          "value": 8,
          "min": 1
        }
      ]
    },
    {
      "type": "accordion",
      "label": "Transfer function",
      "children": [
        {
          "type": "panel",
          "bind": "tfcontainer"
        }
      ]
    }
  ]
}

VolumeLoadDialog:
{
  "type": "panel",
  "children": [
    {
      "type": "field",
      "label": "Type:",
      "children": [
        {
          "type": "dropdown",
          "bind": "type",
          "options": [
            {
              "value": "file",
              "label": "File",
              "selected": true
            },
            {
              "value": "url",
              "label": "URL"
            },
            {
              "value": "demo",
              "label": "Demo"
            }
          ]
        }
      ]
    },
    {
      "type": "panel",
      "bind": "filePanel",
      "visible": true,
      "children": [
        {
          "type": "field",
          "label": "File:",
          "children": [
            {
              "type": "file-chooser",
              "bind": "file"
            }
          ]
        },
        {
          "type": "panel",
          "bind": "rawSettingsPanel",
          "visible": false,
          "children": [
            {
              "type": "field",
              "label": "Dimensions:",
              "children": [
                {
                  "type": "vector",
                  "bind": "dimensions",
                  "value": 128,
                  "min": 1
                }
              ]
            },
            {
              "type": "field",
              "label": "Precision:",
              "children": [
                {
                  "type": "radio",
                  "bind": "precision",
                  "options": [
                    {
                      "value": 8,
                      "label": "8-bit",
                      "selected": true
                    },
                    {
                      "value": 16,
                      "label": "16-bit"
                    },
                    {
                      "value": 32,
                      "label": "32-bit"
                    }
                  ]
                }
              ]
            }
          ]
        }
      ]
    },
    {
      "type": "panel",
      "bind": "urlPanel",
      "visible": false,
      "children": [
        {
          "type": "field",
          "label": "URL:",
          "children": [
            {
              "type": "textbox",
              "bind": "url",
              "placeholder": "http://"
            }
          ]
        }
      ]
    },
    {
      "type": "panel",
      "bind": "demoPanel",
      "visible": false,
      "children": [
        {
          "type": "field",
          "label": "File:",
          "children": [
            {
              "type": "dropdown",
              "bind": "demo"
            }
          ]
        }
      ]
    },
    {
      "type": "panel",
      "bind": "loadButtonAndProgress",
      "visible": false,
      "children": [
        {
          "type": "field",
          "children": [
            {
              "type": "button",
              "bind": "loadButton",
              "label": "Load"
            }
          ]
        },
        {
          "type": "field",
          "children": [
            {
              "type": "progress-bar",
              "bind": "loadProgress"
            }
          ]
        }
      ]
    }
  ]
}
